(()=>{const b=document.body,toggle=document.getElementById("toggleDark"),key="theme";function setMode(m){if(m==="dark"){b.classList.add("modo-escuro","dark-mode");localStorage.setItem(key,"dark");toggle.setAttribute("aria-pressed","true")}else{b.classList.remove("modo-escuro","dark-mode");localStorage.setItem(key,"light");toggle.setAttribute("aria-pressed","false")}}let saved=localStorage.getItem(key)||"light";setMode(saved);toggle.addEventListener("click",()=>{let m=b.classList.contains("dark-mode")?"light":"dark";setMode(m)})})();

